import{_ as t,c as a,o as e,Q as d}from"./chunks/framework.8adc4ca2.js";const m=JSON.parse('{"title":"Linux 包管理","description":"","frontmatter":{"title":"Linux 包管理","order":3},"headers":[],"relativePath":"pages/linux/package.md","lastUpdated":1681724032000}'),s={name:"pages/linux/package.md"},l=d('<h3 id="yum-命令" tabindex="-1">yum 命令 <a class="header-anchor" href="#yum-命令" aria-label="Permalink to &quot;yum 命令&quot;">​</a></h3><h4 id="yum-语法-centos" tabindex="-1">yum 语法（centos） <a class="header-anchor" href="#yum-语法-centos" aria-label="Permalink to &quot;yum 语法（centos）&quot;">​</a></h4><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">yum</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">options</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">command</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">package ...</span><span style="color:#89DDFF;">]</span></span></code></pre></div><ul><li>**options：**可选，选项包括-h（帮助），-y（当安装过程提示选择全部为 &quot;yes&quot;），-q（不显示安装的过程）等等。</li><li>**command：**要进行的操作。</li><li>**package：**安装的包名。</li></ul><hr><h4 id="yum-常用命令" tabindex="-1">yum 常用命令 <a class="header-anchor" href="#yum-常用命令" aria-label="Permalink to &quot;yum 常用命令&quot;">​</a></h4><table><thead><tr><th>yum check-update</th><th>列出所有可更新的软件清单命令</th></tr></thead><tbody><tr><td>yum update</td><td>更新所有软件命令</td></tr><tr><td><code>yum install &lt;package_name&gt;</code></td><td>仅安装指定的软件命令</td></tr><tr><td><code>yum update &lt;package_name&gt;</code></td><td>仅更新指定的软件命令</td></tr><tr><td>yum list</td><td>列出所有可安裝的软件清单命令</td></tr><tr><td><code>yum remove &lt;package_name&gt;</code></td><td>删除软件包命令</td></tr><tr><td><code>yum search &lt;keyword&gt;</code></td><td>查找软件包命令</td></tr><tr><td>yum clean packages</td><td>清除缓存目录下的软件包</td></tr><tr><td>yum clean headers</td><td>清除缓存目录下的 headers</td></tr><tr><td>yum clean oldheaders</td><td>清除缓存目录下旧的 headers</td></tr><tr><td>yum clean, yum clean all (= yum clean packages; yum clean oldheaders)</td><td>清除缓存目录下的软件包及旧的 headers</td></tr></tbody></table><h3 id="apt-命令" tabindex="-1">apt 命令 <a class="header-anchor" href="#apt-命令" aria-label="Permalink to &quot;apt 命令&quot;">​</a></h3><h4 id="apt-语法" tabindex="-1">apt 语法 <a class="header-anchor" href="#apt-语法" aria-label="Permalink to &quot;apt 语法&quot;">​</a></h4><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">apt</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">options</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">command</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">package ...</span><span style="color:#89DDFF;">]</span></span></code></pre></div><ul><li>**options：**可选，选项包括 -h（帮助），-y（当安装过程提示选择全部为&quot;yes&quot;），-q（不显示安装的过程）等等。</li><li>**command：**要进行的操作。</li><li><strong>package</strong>：安装的包名。</li></ul><h4 id="apt-常用命令" tabindex="-1">apt 常用命令 <a class="header-anchor" href="#apt-常用命令" aria-label="Permalink to &quot;apt 常用命令&quot;">​</a></h4><table><thead><tr><th>sudo apt update</th><th>列出所有可更新的软件清单命令</th></tr></thead><tbody><tr><td>sudo apt upgrade</td><td>升级软件包</td></tr><tr><td>apt list --upgradeable</td><td>列出可更新的软件包及版本信息</td></tr><tr><td>sudo apt full-upgrade</td><td>升级软件包，升级前先删除需要更新软件包</td></tr><tr><td><code>sudo apt install &lt;package_name&gt;</code></td><td>安装指定的软件命令</td></tr><tr><td><code>sudo apt install &lt;package_1&gt; &lt;package_2&gt; &lt;package_3&gt;</code></td><td>安装多个软件包</td></tr><tr><td><code>sudo apt install &lt;package_name&gt;</code></td><td>更新指定的软件命令</td></tr><tr><td><code>sudo apt show &lt;package_name&gt;</code></td><td>显示软件包具体信息,例如：版本号，安装大小，依赖关系等等</td></tr><tr><td><code>sudo apt remove &lt;package_name&gt;</code></td><td>删除软件包命令</td></tr><tr><td>sudo apt autoremove</td><td>清理不再使用的依赖和库文件</td></tr><tr><td><code>sudo apt purge &lt;package_name&gt;</code></td><td>移除软件包及配置文件</td></tr><tr><td><code>sudo apt search &lt;package_name&gt;</code></td><td>查找软件包命令</td></tr><tr><td>apt list --installed</td><td>列出所有已安装的包</td></tr><tr><td>apt list --all-versions</td><td>列出所有已安装的包的版本信息</td></tr></tbody></table>',13),o=[l];function r(n,p,c,i,u,h){return e(),a("div",null,o)}const g=t(s,[["render",r]]);export{m as __pageData,g as default};
